    responsive: true,
    maintainAspectRatio: false,
    plugins: {
        legend: { position: 'top', labels: { color: 'white' } },
        // TRANSLATED
        title: { display: true, text: `Price Trends and Model Predictions`, color: 'white' }
    },
    scales: {
        x: {
            type: 'time',
            time: { unit: 'day', tooltipFormat: 'yyyy-MM-dd' },
            // TRANSLATED
