import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

function StockDetails() {
  const { ticker } = useParams();
  const [stockData, setStockData] = useState(null);
  const [news, setNews] = useState(null);
  const [prediction, setPrediction] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);
      try {
        const [detailsRes, newsRes, predictionRes] = await Promise.all([
          fetch(`/api/stocks/${ticker}`),
          fetch(`/api/stocks/${ticker}/news`),
          fetch(`/api/predict/${ticker}`),
        ]);

        if (!detailsRes.ok || !newsRes.ok || !predictionRes.ok) {
          throw new Error('获取数据失败');
        }

        const detailsData = await detailsRes.json();
        const newsData = await newsRes.json();
        const predictionData = await predictionRes.json();

        setStockData(detailsData);
        setNews(newsData);
        setPrediction(predictionData);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, [ticker]);

  if (loading) return <div className="loading">正在加载股票数据...</div>;
  if (error) return <div className="error">错误: {error}</div>;
  if (!stockData) return null;

  const chartData = {
    labels: stockData.map(data => data.date),
    datasets: [
      {
        label: `${ticker} 收盘价`,
        data: stockData.map(data => data.close),
        borderColor: 'rgb(75, 192, 192)',
        backgroundColor: 'rgba(75, 192, 192, 0.5)',
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: `${ticker} 股价走势`,
      },
      tooltip: {
        callbacks: {
          label: function (context) {
            const index = context.dataIndex;
            const dataPoint = stockData[index];
            if (!dataPoint) return '';
            return [
              `收盘: ${dataPoint.close.toFixed(2)}`,
              `开盘: ${dataPoint.open.toFixed(2)}`,
              `最高: ${dataPoint.high.toFixed(2)}`,
              `最低: ${dataPoint.low.toFixed(2)}`,
            ];
          },
        },
      },
    },
  };

  return (
    <div className="stock-details-container">
      <h2>{ticker} - 股票详情</h2>

      <section className="chart-prediction-section">
        <div className="chart-container">
          <Line options={chartOptions} data={chartData} />
        </div>

        {prediction && (
          <div className="prediction-container">
            <h3>模型预测明日收盘价</h3>
            <p className="prediction-price">${prediction.predicted_close}</p>
            <small>（基于简单线性回归模型）</small>
          </div>
        )}
      </section>

      <section className="news-section">
        {news && (
          <>
            <div className="news-list-container">
              <h3>最新资讯</h3>
              {news.news_items.map(item => (
                <div key={item.id} className="news-item">
                  <p className="news-title">{item.title}</p>
                  <p className="news-date">{item.date}</p>
                  <p className="news-summary">{item.summary}</p>
                </div>
              ))}
            </div>
            <div className="news-summary-container">
              <h3>近期资讯总结</h3>
              <p>{news.summary}</p>
            </div>
          </>
        )}
      </section>
    </div>
  );
}

export default StockDetails;
