// StockDetails.js 

import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Chart } from 'react-chartjs-2';
import {
  Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement,
  Title, Tooltip, Legend, TimeScale
} from 'chart.js';
import 'chartjs-adapter-date-fns';

ChartJS.register(
  CategoryScale, LinearScale, PointElement, LineElement,
  Title, Tooltip, Legend, TimeScale
);

const modelColors = [
    'rgba(255, 99, 132, 1)', // Red
    'rgba(54, 162, 235, 1)', // Blue
    'rgba(255, 206, 86, 1)', // Yellow
];

// Extract chart options to make the code cleaner
const chartOptions = {
    responsive: true,
    maintainAspectRatio: false, // Allows the chart to not maintain its aspect ratio, fitting the container better
    plugins: {
        legend: { position: 'top' },
        title: { display: true, text: `Price Trends and Predictions` }
    },
    scales: {
        x: {
            type: 'time',
            time: { unit: 'day', tooltipFormat: 'yyyy-MM-dd' },
            title: { display: true, text: 'Date' }
        },
        y: {
            title: { display: true, text: 'Price' }
        }
    }
};

function StockDetails() {
    const { ticker } = useParams();
    const [chartData, setChartData] = useState(null);
    const [news, setNews] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        setLoading(true);
        setError(null); // Reset error state on each load

        fetch(`/api/stocks/${ticker}`)
            .then(response => {
                if (!response.ok) {
                    return Promise.reject(`Server error: ${response.status}`);
                }
                return response.json();
            })
            .then(data => {
                if (data.error) {
                    return Promise.reject(data.error);
                }

                // This is the key fix: populating the full chart data processing logic
                const historyDataset = {
                    label: 'Close Price',
                    data: data.history.map(item => ({ x: item.date, y: item.close })),
                    borderColor: 'rgb(75, 192, 192)',
                    backgroundColor: 'rgba(75, 192, 192, 0.5)',
                    tension: 0.1
                };

                const predictionDatasets = Object.keys(data.predictions).map((modelName, index) => ({
                    label: `${modelName} Prediction`,
                    data: data.predictions[modelName].map(p => ({ x: p.date, y: p.price })),
                    borderColor: modelColors[index % modelColors.length],
                    backgroundColor: modelColors[index % modelColors.length],
                    showLine: false,
                    pointRadius: 6,
                    pointHoverRadius: 8,
                }));

                setChartData({
                    datasets: [historyDataset, ...predictionDatasets],
                });

                setNews(data.news || []);

            })
            .catch(err => {
                console.error(`Failed to fetch stock details for ${ticker}:`, err);
                setError(typeof err === 'string' ? err : 'An unknown error occurred while fetching data.');
            })
            .finally(() => {
                setLoading(false);
            });
    }, [ticker]);

    if (loading) return <div style={{ textAlign: 'center', marginTop: '50px' }}>Loading data for {ticker}...</div>;
    if (error) return <div style={{ color: '#ff4d4d', textAlign: 'center', marginTop: '50px' }}>Error: {error}</div>;

    return (
        <div>
            <h2>{ticker} Historical Prices and Model Predictions</h2>
            <div className="details-container">
                {/* Left Chart Container */}
                <div className="chart-container">
                    {chartData ? (
                        <div style={{ position: 'relative', height: '400px' }}>
                             <Chart type='line' data={chartData} options={chartOptions} />
                        </div>
                    ) : (
                        <div>Preparing chart...</div>
                    )}
                </div>

                {/* Right News Container */}
                <div className="news-container">
                    <h3>Related News</h3>
                    <div className="news-list">
                        {news.length > 0 ? (
                            news.map((item, index) => (
                                <div key={index} className="news-item">
                                    <h4>{item.title}</h4>
                                    <p>{item.summary}</p>
                                    {/* Format the date to be more user-friendly */}
                                    <span>{new Date(item.date).toLocaleDateString()}</span>
                                </div>
                            ))
                        ) : (
                            <p>No related news available.</p>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default StockDetails;
