// StockDetails.js (Âú®ÊÇ®ÂéüÊúâ‰ª£Á†ÅÂü∫Á°Ä‰∏ä‰ªÖÂÅöÂ¢ûË°•)

import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Chart } from 'react-chartjs-2';
import {
  Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement,
  Title, Tooltip, Legend, TimeScale
} from 'chart.js';
import 'chartjs-adapter-date-fns';

// --- ÊÇ®ÂéüÊúâÁöÑ‰ª£Á†Å (ÂÆåÂÖ®‰∏çÂèò) ---
ChartJS.register(
  CategoryScale, LinearScale, PointElement, LineElement,
  Title, Tooltip, Legend, TimeScale
);
const modelColors = [
    'rgba(255, 99, 132, 1)', 'rgba(54, 162, 235, 1)', 'rgba(255, 206, 86, 1)',
];
const chartOptions = {
    responsive: true, maintainAspectRatio: false,
    plugins: { legend: { position: 'top' }, title: { display: true, text: `Price Trends and Predictions` } },
    scales: {
        x: { type: 'time', time: { unit: 'day', tooltipFormat: 'yyyy-MM-dd' }, title: { display: true, text: 'Date' } },
        y: { title: { display: true, text: 'Price' } }
    }
};

function StockDetails() {
    const { ticker } = useParams();
    // --- ÊÇ®ÂéüÊúâÁöÑÁä∂ÊÄÅ (ÂÆåÂÖ®‰∏çÂèò) ---
    const [chartData, setChartData] = useState(null);
    const [news, setNews] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);
    const [isNewsExpanded, setIsNewsExpanded] = useState(false);

    // --- ‚òÖ‚òÖ‚òÖ Êñ∞Â¢ûÁöÑÁä∂ÊÄÅ: Áî®‰∫éAIÊëòË¶Å ‚òÖ‚òÖ‚òÖ ---
    const [historicalData, setHistoricalData] = useState(null); // Áî®‰∫éÂèëÈÄÅÁªôAIÂêéÁ´Ø
    const [aiSummary, setAiSummary] = useState('');
    const [isAiLoading, setIsAiLoading] = useState(false);
    const [aiError, setAiError] = useState(null);

    // --- ÊÇ®ÂéüÊúâÁöÑÂáΩÊï∞ (ÂÆåÂÖ®‰∏çÂèò) ---
    const toggleNews = () => setIsNewsExpanded(!isNewsExpanded);

    // --- ÊÇ®ÂéüÊúâÁöÑ useEffect (‰ªÖË°•ÂÖÖ‰∏ÄË°å‰ª£Á†Å) ---
    useEffect(() => {
        setLoading(true);
        setError(null);
        fetch(`/api/stocks/${ticker}`)
            .then(response => {
                if (!response.ok) return Promise.reject(`Server error: ${response.status}`);
                return response.json();
            })
            .then(data => {
                if (data.error) return Promise.reject(data.error);

                const historyDataset = {
                    label: 'Close Price',
                    data: data.history.map(item => ({ x: item.date, y: item.close })),
                    borderColor: 'rgb(75, 192, 192)',
                    backgroundColor: 'rgba(75, 192, 192, 0.5)',
                    tension: 0.1
                };
                const predictionDatasets = Object.keys(data.predictions).map((modelName, index) => ({
                    label: `${modelName} Prediction`,
                    data: data.predictions[modelName].map(p => ({ x: p.date, y: p.price })),
                    borderColor: modelColors[index % modelColors.length],
                    backgroundColor: modelColors[index % modelColors.length],
                    showLine: false, pointRadius: 6, pointHoverRadius: 8,
                }));
                setChartData({ datasets: [historyDataset, ...predictionDatasets] });
                setNews(data.news || []);

                // --- ‚òÖ‚òÖ‚òÖ ÂîØ‰∏ÄË°•ÂÖÖÁöÑ‰∏ÄË°å: ‰øùÂ≠òÂéÜÂè≤Êï∞ÊçÆ‰ª•Ëß¶ÂèëAIÂàÜÊûê ‚òÖ‚òÖ‚òÖ ---
                setHistoricalData(data.history);
            })
            .catch(err => {
                console.error(`Failed to fetch stock details for ${ticker}:`, err);
                setError(typeof err === 'string' ? err : 'An unknown error occurred while fetching data.');
            })
            .finally(() => setLoading(false));
    }, [ticker]);

    // --- ‚òÖ‚òÖ‚òÖ Êñ∞Â¢ûÁöÑ useEffect: ‰∏ìÈó®Áî®‰∫éËé∑ÂèñAIÊëòË¶Å ‚òÖ‚òÖ‚òÖ ---
    useEffect(() => {
        if (historicalData && historicalData.length > 0) {
            setIsAiLoading(true);
            setAiError(null);
            fetch('/api/summarize', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ historicalData, ticker })
            })
            .then(response => {
                if (!response.ok) return Promise.reject('Failed to get AI summary from server.');
                return response.json();
            })
            .then(data => {
                if (data.error) return Promise.reject(data.error);
                setAiSummary(data.summary);
            })
            .catch(err => {
                console.error("AI summary fetch error:", err);
                setAiError(typeof err === 'string' ? err : 'Êó†Ê≥ïÂä†ËΩΩAIÂàÜÊûêÊëòË¶Å„ÄÇ');
            })
            .finally(() => setIsAiLoading(false));
        }
    }, [historicalData, ticker]); // ÂΩìÂéÜÂè≤Êï∞ÊçÆÂä†ËΩΩÂÆåÊØïÂêéËß¶Âèë

    // --- ÊÇ®ÂéüÊúâÁöÑÊ∏≤ÊüìÈÄªËæë (ÂÆåÂÖ®‰∏çÂèò) ---
    if (loading) return <div style={{ textAlign: 'center', marginTop: '50px' }}>Loading data for {ticker}...</div>;
    if (error) return <div style={{ color: '#ff4d4d', textAlign: 'center', marginTop: '50px' }}>Error: {error}</div>;

    // --- ÊÇ®ÂéüÊúâÁöÑ JSX (‰ªÖÂ¢ûÂä†‰∏Ä‰∏™AIÊëòË¶ÅÁöÑdiv) ---
    return (
        <div className="stock-details-page-container">
            <h2 className="stock-details-subtitle">
                {ticker} Historical Prices and Model Predictions
            </h2>
            <div className="details-container">
                <div className="chart-container">
                    {chartData ? (
                        <div style={{ position: 'relative', height: '400px' }}>
                             <Chart type='line' data={chartData} options={chartOptions} />
                        </div>
                    ) : ( <div>Preparing chart...</div> )}
                </div>

                {/* --- ‚òÖ‚òÖ‚òÖ Êñ∞Â¢ûÁöÑAIÊô∫ËÉΩÂàÜÊûêÂÆπÂô® ‚òÖ‚òÖ‚òÖ --- */}
                <div className="ai-summary-container" style={{marginTop: '20px', padding: '15px', backgroundColor: '#f8f9fa', borderRadius: '8px'}}>
                    <h3 className="ai-summary-header">üìà AI Êô∫ËÉΩÂàÜÊûê</h3>
                    {isAiLoading && <p>Ê≠£Âú®ÁîüÊàêAIÊëòË¶ÅÔºåËØ∑Á®çÂÄô...</p>}
                    {aiError && <p style={{color: 'red'}}>{aiError}</p>}
                    {aiSummary && <p style={{whiteSpace: 'pre-wrap', lineHeight: '1.6'}}>{aiSummary}</p>}
                </div>

                {/* --- ÊÇ®ÂéüÊúâÁöÑÊñ∞ÈóªÂÆπÂô® (ÂÆåÂÖ®‰∏çÂèò) --- */}
                <div className="news-container">
                    <h3 onClick={toggleNews} className="news-header">
                        Related News <span>{isNewsExpanded ? '‚ñ≤' : '‚ñº'}</span>
                    </h3>
                    {isNewsExpanded && (
                        <div className="news-list">
                            {news.length > 0 ? (
                                news.map((item, index) => (
                                    <div key={index} className="news-item">
                                        <div className="news-content">
                                            <h4>{item.title}</h4>
                                            <p>{item.summary}</p>
                                        </div>
                                        <span className="news-date">{new Date(item.date).toLocaleDateString()}</span>
                                    </div>
                                ))
                            ) : ( <p>No related news available.</p> )}
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
}

export default StockDetails;
