import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import HangSengChart from './HangSengChart'; // <-- 1. 导入新的图表组件

// 注意：我们已经移除了 useNavigate, useState for searchTerm, handleSearch, 和 handleKeyPress
// 因为这些逻辑现在由 App.js 处理

function StockList() {
  const [stocks, setStocks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch('/api/stocks')
      .then(res => res.json())
      .then(data => {
        setStocks(data);
        setLoading(false);
      })
      .catch(error => {
        console.error("Failed to fetch stock list:", error);
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <div>Loading stock list...</div>;
  }

  // 2. 移除了整个搜索框的 JSX
  return (
    <div className="stock-list-container">
      {/* 3. 在列表上方添加恒生指数图表 */}
      <HangSengChart />

      <hr />

      <h2>Or select a stock below</h2>
      <ul>
        {stocks.map(stock => (
          <li key={stock.ticker}>
            <Link to={`/stock/${stock.ticker}`}>
              {stock.ticker}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default StockList;
